mapper.insert:
insert
  into address
     ( 
       ADD_NO
	 , EMP_NO
	 , ADD_TYPE
	 , ADD_NAME
	 , EMAIL
	 , PHONE
	 , ADD_EMP
	 , ADD_DEPT
	 , ADD_JOB
	 , GROUP_NO
	 , EMP_PHONE
	 , EMP_ADDRESS
	 , MEMO
	 , CREATE_DATE
     )
values
	 (
	   seq_addno.nextval
	 , #{empNo}
	 , 'P'
	 , #{addName}
	 , #{email}
	 , #{phone}
	 , #{addEmp}
	 , #{addDept}
	 , #{addJob}
	 , #{groupNo}
	 , #{empPhone}
	 , #{empAddress}
	 , #{memo}
	 , sysdate
	 )

