mapper.select:
SELECT add_emp_no
  FROM ADD_FAV
 WHERE EMP_NO = #{no}

select
       count(*)
  from employee
 where ent_yn = 'N'
   and emp_no != #{empNo}
   and dept_code = #{deptCode}

select count(*) from address where emp_no = #{empNo}
and group_no = #{groupNo}
and group_no is null
and add_type = 'P' and add_bin = 'N'

select ADD_NO , a.EMP_NO , a.ADD_TYPE , ADD_NAME , EMAIL , PHONE , ADD_EMP , ADD_DEPT , ADD_JOB , a.GROUP_NO , GROUP_NAME , EMP_PHONE , EMP_ADDRESS , MEMO , CREATE_DATE , MODIFY_DATE , DELETE_DATE , ADD_BIN , EDITABLE from address a left join add_group g on (a.group_no = g.group_no) where a.emp_no = #{empNo}
and a.group_no = #{groupNo}
and a.group_no is null
and a.add_type = 'P' and add_bin = 'N' order by create_date

select count(*)
  from add_group
 where emp_no = #{empNo}
   and group_name = #{groupName}
   and add_type = 'P'

mapper.update:
	update
	       address
	   set
	       ADD_NAME = #{addName}
	     , EMAIL = #{email}
	     , PHONE = #{phone}
	     , ADD_EMP = #{addEmp}
	     , ADD_DEPT = #{addDept}
	     , ADD_JOB = #{addJob}
	     , GROUP_NO = #{groupNo}
	     , EMP_PHONE = #{empPhone}
	     , EMP_ADDRESS = #{empAddress}
	     , MEMO = #{memo}
	     , MODIFY_DATE = sysdate
    where add_no = #{addNo}

mapper.delete:
