mapper.select:
select * from( select rownum rnum, a.* from( select app_no
, to_char(enroll_date, 'YYYY-MM-DD') as "enroll_date" , app_tstatus , title , b.emp_no , form_name , form_code , app_amount , app_sequence , emp_name from approval b join employee e on (b.emp_no = e.emp_no) where b.status = '1' and app_tstatus = '진행중' and app_no IN ( select app_no from app_line where ref_whether = 'N' and app_status ='미결재' and emp_no = #{eNo} and app_seq_no = b.app_sequence ) order by b.enroll_date
)a ) where rnum between 1 and 4

select APP_NO , to_char(a.ENROLL_DATE, 'YYYY-MM-dd') as "enroll_date" , a.STATUS , APP_TSTATUS , WRITER_COMMENT , TITLE , CONTENT , a.EMP_NO , FORM_NAME , CONSER_PERIOD , SEC_GRADE , FORM_CODE , APP_AMOUNT , APP_SEQUENCE , APP_CHANGE , emp_name , dept_name , job_code from approval a join employee b on (a.emp_no = b.emp_no) join department c on (b.dept_code = c.dept_code) where a.status = '1'
and a.emp_no = #{writerNo}
and app_tstatus = #{tstatus}
and app_no = #{appNo}

mapper.insert:
 	insert
