mapper.select:
select APP_NO , to_char(a.ENROLL_DATE, 'YYYY-MM-dd') as "enroll_date" , a.STATUS , APP_TSTATUS , WRITER_COMMENT , TITLE , CONTENT , a.EMP_NO , FORM_NAME , CONSER_PERIOD , SEC_GRADE , FORM_CODE , APP_AMOUNT , APP_SEQUENCE , APP_CHANGE , emp_name , dept_name , job_code from approval a join employee b on (a.emp_no = b.emp_no) join department c on (b.dept_code = c.dept_code) where app_no = #{appNo}
and APP_TSTATUS = '진행중'
and a.status = '1'

mapper.update:
update 
       app_line
   set count = count+1
 where app_line_no = (
                        select app_line_no
                          from app_line
                         where ref_whether = 'Y'
                           and emp_no = #{receiverNo}
                           and app_no = #{appNo}
                        )

mapper.insert:
insert
  into approval
  (
     app_no
   , enroll_date
   , writer_comment
   , title
   , content
   , emp_no
   , form_name
   , conser_period
   , sec_grade
   , form_code
   , app_amount
   , app_sequence
   , app_change
  )
    values
  (
     seq_apno.nextval
   , #{enrollDate}
   , #{writerComment}
   , #{title}
   , #{content}
   , #{writerNo}
   , #{formName}
   , #{conPeriod}
   , #{secGrade}
   , #{formCode}
   , #{appAmount}
   , #{appSequence}
   , #{appChange}
  )

insert
  into vac_form
  (
  app_no
  , vac_kind
  , vac_start
  , vac_end
  , half_status
  , vac_use
  , half_day
  )
  values
  (
      (
        select max(app_no)
          from approval
      )
    , #{vacKind}
    , #{vacStart}
    , #{vacEnd}
    , #{halfStatus}
    , #{vacUse}
    , #{halfDay}
  )

insert
  into ot_form
      (
      app_no
      , overtime_kind
      , over_date
      , over_start_hour
      , over_end_hour
      , over_use_time
      )
        values
     (
         (
            select max(app_no)
              from approval
         )
       , #{otKind}
       , #{otDate}
       , #{otStart}
       , #{otEnd}
       , #{otUseTime}
     )

mapper.update:
update 
        approval
   set app_tstatus = #{appStatus}
 where app_amount = app_sequence
   and app_no = #{appNo}

update app_line
   set app_status = #{appStatus}
      , update_date = sysdate
      , app_comment = #{appComment}
  where app_line_no = (
                        select app_line_no
                           from app_line
                        where emp_no = #{recEmpNo}
                          and app_no = #{appNo}
                          and ref_whether = 'N'
                        )

		   update
                   approval
              set
                     enroll_date = #{enrollDate}
                   , writer_comment = #{writerComment}
                   , title = #{title}
                   , content = #{content}
                   , app_amount = #{appAmount}
                   , app_sequence = #{appSequence}
                   , app_change = #{appChange}
                   , status = #{status}     
            where  app_no = #{appNo}
