mapper.select:
select
	   count(*)
  from employee
 where emp_name = #{empName}
   and phone = #{phone}

mapper.update:
update 
	    employee
   set phone = #{phone},
       address = #{address}
 where emp_no = #{empNo}

update
       employee
   set emp_pwd = #{empPwd}
 where emp_id = #{empId}

update
	   employee
   set emp_profile = #{empProfile}
 where emp_id = #{empId}

update
       employee
  set emp_profile = #{empProfile}
where emp_no = #{empNo}
