mapper.insert:
	insert 
	  into mail_tag
  	  (
  	    tag_no
  	  , emp_no
  	  , tag_name
  	  , tag_color  
  	  )
	  values
	  (
	    seq_tagno.nextval
	  , #{empNo}
	  , #{tagName}
	  , #{tagColor}
	  )

insert 
  into send_mail
  (
  	MAIL_NO
  , SEND_MAIL_ADDRESS
  , MAIL_TITLE
  , MAIL_CONTENT
  , SENT_DATE
  , TEMP_STORAGE
  )
  values
  (
    seq_mano.nextval
  , #{ sendMailAdd }
  , #{ mailTitle }
  , #{ mailContent }
  , to_char(sysdate, 'YYYY-MM-DD DY HH24:mi:SS')
  , #{ tempStorage }
  )

insert
  into rec_mail
  (
	RECEP_MAIL_NO
  , MAIL_NO
  , RECEP_MAIL_ADDRESS
  , SEND_MAIL_ADDRESS
  , MAIL_TITLE
  , MAIL_CONTENT
  , IMPOR_MAIL
  , REFERENCE
  , HIDDEN_REFERENCE
  , status
  )
  values
  (
    seq_recmano.nextval
  , seq_mano.currval
  , #{ recMailAdd }
  , #{ sendMailAdd }
  , #{ mailTitle }
  , #{ mailContent }
  , #{ imporMail }
  , #{ reference }
  , #{ hiddenReference }
  , #{ status }
  )

mapper.update:
update rec_mail 
   set rec_check = 'Y'
     , rec_date = to_char(sysdate, 'YYYY-MM-DD DY HH24:mi:SS')
 where mail_no = #{ mailNo }
   and recep_mail_address = #{ recMailAdd }

update rec_mail
   set 
       tag_no = #{ tagNo }
 where recep_mail_no = #{ recMailNo }

update send_mail
   set status = 'N'
 where mail_no = #{mailNo}

update rec_mail
   set impor_mail = #{ imporMail }
 where recep_mail_no = #{ recMailNo }

mapper.delete:
delete
  from mail_fav
 where emp_no = #{ empNo }
   and mailbox_name = #{ mailBoxName }
